def countTreeHitsOnSlope(rStep,dStep):
    step = 1
    treeHits = 0
    for i in range(0,len(input),dStep):
        if("#" == input[i][((step % len(input[i])) - 1)]):
            treeHits +=1
        step += rStep
    return treeHits

